import React, { useState } from "react";

const App = () => {
  // hook function in react
  let Time = new Date().toLocaleTimeString();

  const [newTime, setTime] = useState(Time);
  const IncNum = () => {
    setTime({Time});
  };

  return (
    <>
      <h1>{Time}</h1>
      <button onClick={IncNum}>click me</button>
    </>
  );
};
export default App;
